</span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;rgbcolor</span></span><span class="cmtt-10"><span class="p">&nbsp;</span><span class="s">"#RRGGBB"</span></span><span class="p">
</span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;rgbcolor</span></span><span class="cmtt-10"><span class="p">&nbsp;variable</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span><span class="C">#</span></span><span class="cmtt-10"><span class="C">&nbsp;color</span></span><span class="cmtt-10"><span class="C">&nbsp;is</span></span><span class="cmtt-10"><span class="C">&nbsp;read</span></span><span class="cmtt-10"><span class="C">&nbsp;from</span></span><span class="cmtt-10"><span class="C">&nbsp;input</span></span><span class="cmtt-10"><span class="C">&nbsp;file</span></span><span class="p">
