</span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span><span class="o">{</span></span><span class="cmtt-10"><span class="o">&nbsp;</span><span class="p">textcolor</span></span><span class="cmtt-10"><span class="p">&nbsp;</span><span class="s">&lt;colorspec&gt;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span><span class="o">}</span></span><span class="p">
</span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span><span class="k">set</span></span><span class="cmtt-10"><span class="p">&nbsp;tics</span></span><span class="cmtt-10"><span class="p">&nbsp;</span><span class="o">{</span><span class="p">front</span></span><span class="cmtt-10"><span class="p">&nbsp;</span><span class="o">|</span></span><span class="cmtt-10"><span class="o">&nbsp;</span><span class="p">back</span><span class="o">}</span></span><span class="p">
</span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span><span class="k">unset</span></span><span class="cmtt-10"><span class="p">&nbsp;tics</span></span><span class="p">
</span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span></span><span class="cmtt-10"><span class="p">&nbsp;</span><span class="k">show</span></span><span class="cmtt-10"><span class="p">&nbsp;tics</span></span>
